package com.active.admin;import com.active.common.utils.CookieUtil;import com.active.common.utils.Md5Util;import com.alibaba.fastjson.JSONObject;import org.apache.commons.lang3.StringUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * 这是一个类说明 * * @author ta * @date 2019/7/4 上午12:11 * @since 1.0 */@Controller@RequestMapping("/activeLogin")public class LoginController {    private final static Logger logger = LoggerFactory.getLogger(LoginController.class);    @RequestMapping("doLogin")    @ResponseBody    public String doLogin(HttpServletRequest request, HttpServletResponse response){        JSONObject res = new JSONObject();        res.put("code", 0);        res.put("msg", "success");        try {            String username = request.getParameter("username");            String password = request.getParameter("password");            if(StringUtils.isNotBlank(username) && StringUtils.isNotBlank(password)){                if(!"admin".equals(username)){                    res.put("code", -3);                    res.put("msg", "用户名不正确");                    return res.toJSONString();                }                if(!"!@#admin".equals(password)){                    res.put("code", -4);                    res.put("msg", "密码不正确");                    return res.toJSONString();                }                CookieUtil.addCookie("customer", Md5Util.MD5(username+password),response);                res.put("code", 0);                res.put("msg", "success");            }else{                res.put("code", -2);                res.put("msg", "参数错误");                return res.toJSONString();            }        }catch (Exception e){            logger.error("error："+e.getMessage());            res.put("code", -1);            res.put("msg", "error");        }        return res.toJSONString();    }}