package com.active.common.utils;import java.util.regex.Matcher;import java.util.regex.Pattern;/** * 这是一个类说明 * * @author ta * @date 2019/7/1 下午10:59 * @since 1.0 */public class ValidateUtil {    public static boolean checkMobileNumber(String mobileNumber) {        boolean flag = false;        try {            Pattern regex = Pattern.compile("^1[345789]\\d{9}$");            Matcher matcher = regex.matcher(mobileNumber);            flag = matcher.matches();        } catch (Exception e) {            e.printStackTrace();            flag = false;        }        return flag;    }}