package com.active.front.controller;import com.active.bean.RoadDriverInfo;import com.active.bean.RoadExportDriverInfo;import com.active.bean.VisitExportInfo;import com.active.common.utils.ExportExcelUtil;import com.active.common.utils.ExportExcelWrapper;import com.active.front.service.RoadDriverService;import com.active.front.service.VisitService;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.util.List;/** * 这是一个类说明 * @author ta * @date 2019/7/9 下午7:26 * @since 1.0 */@Controller@RequestMapping("/export")public class ExportController {    @Autowired    private VisitService visitService;    @Autowired    private RoadDriverService roadDriverService;    @RequestMapping("/getExcel")    public void getExcel(HttpServletRequest request, HttpServletResponse response) throws Exception {        String[] columnNames = { "ID", "姓名", "手机号","年龄段","CDSID或者邮箱","参观时间","报名时间"};        String fileName = "member";        List<VisitExportInfo> list = visitService.getVisitExportInfos();        ExportExcelWrapper<VisitExportInfo> util = new ExportExcelWrapper<VisitExportInfo>();        util.exportExcel(fileName, fileName, columnNames, list, response, ExportExcelUtil.EXCEl_FILE_2007);    }    @RequestMapping("/getExcelDz")    public void getExcelDz(HttpServletRequest request, HttpServletResponse response) throws Exception {        String[] columnNames = { "ID", "姓名", "手机号","年龄段","职业","参观时间","报名时间"};        String fileName = "member";        List<VisitExportInfo> list = visitService.getVisitExportDzInfos();        ExportExcelWrapper<VisitExportInfo> util = new ExportExcelWrapper<VisitExportInfo>();        util.exportExcel(fileName, fileName, columnNames, list, response, ExportExcelUtil.EXCEl_FILE_2007);    }    @RequestMapping("/getExcelRoad")    public void getExcelRoad(HttpServletRequest request, HttpServletResponse response) throws Exception {        String[] columnNames = { "姓名", "手机号","省","市","经销商地址","车型","预约时间","区号","经销商电话","创建时间"};        String fileName = "info";        RoadDriverInfo roadDriverInfo = new RoadDriverInfo();        String mobile =request.getParameter("mobile");        if(StringUtils.isNotBlank(mobile)){            roadDriverInfo.setMobile(mobile);        }        List<RoadExportDriverInfo> list = roadDriverService.getExcelList(roadDriverInfo);        ExportExcelWrapper<RoadExportDriverInfo> util = new ExportExcelWrapper<RoadExportDriverInfo>();        util.exportExcel(fileName, fileName, columnNames, list, response, ExportExcelUtil.EXCEl_FILE_2007);    }}