package com.active.front.service;import com.active.bean.DriveInfo;import com.active.front.dao.DriveDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * 这是一个类说明 * * @author ta * @date 2019/7/1 下午11:22 * @since 1.0 */@Servicepublic class DriveService {    @Autowired    private DriveDao driveDao;    public void insertInfo(DriveInfo driveInfo) {        driveDao.insertInfo(driveInfo);    }    public DriveInfo getInfo(DriveInfo driveInfo) {        return driveDao.getInfo(driveInfo);    }    public void updateInfo(DriveInfo driveInfo) {        driveDao.updateInfo(driveInfo);    }    public Void deleteInfo(DriveInfo driveInfo) {        return driveDao.deleteInfo(driveInfo);    }}