package com.active.front.service;import com.active.bean.CityInfo;import com.active.bean.DealerInfo;import com.active.bean.ProvinceInfo;import com.active.bean.RoadDriverInfo;import com.active.front.dao.RoadDriverDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * 这是一个类说明 * * @author ta * @date 2019/8/18 下午8:52 * @since 1.0 */@Servicepublic class RoadDriverService {    @Autowired    private RoadDriverDao roadDriverDao;    public List<ProvinceInfo> getProvinceInfos(){       return roadDriverDao.getProvinceInfos();    }    public List<CityInfo> getCityInfos(ProvinceInfo provinceInfo){        return roadDriverDao.getCityInfos(provinceInfo);    }    public List<DealerInfo> getDealerInfos(CityInfo cityInfo){        return roadDriverDao.getDealerInfos(cityInfo);    }    public List<RoadDriverInfo> getList(RoadDriverInfo roadDriverInfo){        return roadDriverDao.getList(roadDriverInfo);    }    public Integer getCount(RoadDriverInfo roadDriverInfo){        return roadDriverDao.getCount(roadDriverInfo);    }    public RoadDriverInfo getInfo(RoadDriverInfo roadDriverInfo){        return roadDriverDao.getInfo(roadDriverInfo);    }    public void insertInfo(RoadDriverInfo roadDriverInfo){         roadDriverDao.insertInfo(roadDriverInfo);    }    public void updateInfo(RoadDriverInfo roadDriverInfo){        roadDriverDao.updateInfo(roadDriverInfo);    }    public void deleteInfo(Long id){        roadDriverDao.deleteInfo(id);    }}