package com.active.front.service;import com.active.bean.*;import com.active.front.dao.RoadV2DriverDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * 这是一个类说明 * * @author ta * @date 2019/8/18 下午8:52 * @since 1.0 */@Servicepublic class RoadV2DriverService {    @Autowired    private RoadV2DriverDao roadV2DriverDao;    public List<ProvinceInfo> getProvinceInfos(){       return roadV2DriverDao.getProvinceInfos();    }    public List<CityInfo> getCityInfos(ProvinceInfo provinceInfo){        return roadV2DriverDao.getCityInfos(provinceInfo);    }    public List<DealerInfo> getDealerInfos(CityInfo cityInfo){        return roadV2DriverDao.getDealerInfos(cityInfo);    }    public List<RoadDriverInfo> getList(RoadDriverInfo roadDriverInfo){        return roadV2DriverDao.getList(roadDriverInfo);    }    public Integer getCount(RoadDriverInfo roadDriverInfo){        return roadV2DriverDao.getCount(roadDriverInfo);    }    public RoadDriverInfo getInfo(RoadDriverInfo roadDriverInfo){        return roadV2DriverDao.getInfo(roadDriverInfo);    }    public void insertInfo(RoadDriverInfo roadDriverInfo){        roadV2DriverDao.insertInfo(roadDriverInfo);    }    public void updateInfo(RoadDriverInfo roadDriverInfo){        roadV2DriverDao.updateInfo(roadDriverInfo);    }    public void deleteInfo(Long id){        roadV2DriverDao.deleteInfo(id);    }    public List<RoadExportDriverInfo> getExcelList(RoadDriverInfo roadDriverInfo) {        return roadV2DriverDao.getExcelList(roadDriverInfo);    }    public List<RoadJdbbInfo> getJdList(RoadJdbbInfo roadJdbbInfo){        return roadV2DriverDao.getJdList(roadJdbbInfo);    }    public void insertJdInfo(RoadJdbbInfo roadJdbbInfo){        roadV2DriverDao.insertJdInfo(roadJdbbInfo);    }    public RoadJdbbInfo getJdInfo(RoadJdbbInfo roadJdbbInfo) {        return roadV2DriverDao.getJdInfo(roadJdbbInfo);    }    public Integer getJdCount(RoadJdbbInfo roadJdbbInfo) {       return roadV2DriverDao.getJdCount(roadJdbbInfo);    }}