package com.active.front.service;import com.active.bean.TgInfo;import com.active.front.dao.TgDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * 这是一个类说明 * * @author * @date 2019/11/16 上午10:49 * @since 1.0 */@Servicepublic class TgService {    @Autowired    private TgDao tgDao;    public TgInfo getInfo(String mobile){        return tgDao.getInfo(mobile);    }    public int getCount(int status){        return tgDao.getCount(status);    }    public List<TgInfo> getInfos(TgInfo tgInfo){        return tgDao.getInfos(tgInfo);    }    public void addInfo(TgInfo tgInfo){        tgDao.addInfo(tgInfo);    }}